// Deep Partial
type DeepPartial<T> = {
  [P in keyof T]?: T[P] extends object
    ? DeepPartial<T[P]>
    : T[P];
};

// NonEmptyArray
type NonEmptyArray<T> = [T, ...T[]];

// Prettify - Makes types readable
type Prettify<T> = {
  [K in keyof T]: T[K];
} & {};

